const hyphenate = string => string.replace(/([a-zA-Z])([A-Z])/g, '$1-$2').toLowerCase();

const capitalize = string => string.charAt(0).toUpperCase() + string.slice(1);

const camelize = string => string.toLowerCase().replace(/-([a-z])/g, (m, l) => l.toUpperCase());

const eventToHandler = name => '_on' + capitalize(name);

const handlerToProp = name => name.slice(2).charAt(0).toLowerCase() + name.slice(2).slice(1);

export { camelize, capitalize, eventToHandler, handlerToProp, hyphenate };
